pub enum Command<Id, Intro, Rule> {

    /// Introduce a new name
    Intro(Id, Intro),

    /// Add rewrite rules
    Rules(Vec<Rule>),
}

pub enum Intro<Ty, Tm> {
    Definition(Option<Ty>, Option<Tm>),
    Theorem(Ty, Tm),
    Declaration(Ty),
}

pub enum Pattern<S> {

    /// matches an application
    Symb(S, Vec<Pattern<S>>),

    /// matches any term, variable may appear multiple times in
    /// both left-hand and right-hand sides of rewrite rule
    MVar(Miller),

    /// matches any term
    Joker,
}
